                // Update final dpx and rdpx balances for epoch and strike
                totalEpochStrikeRdpxBalance[currentEpoch][strikes[i]] +=
                    totalEpochStrikeDeposits[currentEpoch][strikes[i]] +
                    rdpxRewards -
                    settlement;
                if (accountPremiums) {
                    totalEpochStrikeRdpxBalance[currentEpoch][
                        strikes[i]
