// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

// Contracts
import {Ownable} from '@openzeppelin/contracts/access/Ownable.sol';

// Interfaces
import {IVolatilityOracle} from '../interfaces/IVolatilityOracle.sol';

contract VolatilityOracle is Ownable, IVolatilityOracle {
    /*==== PUBLIC VARS ====*/

    uint256 public lastVolatility;

    /*==== SETTER FUNCTIONS (ONLY OWNER) ====*/

    /**
     * @notice Updates the last volatility for DPX
     * @param v volatility
     * @return volatility of dpx
     */
    function updateVolatility(uint256 v) external onlyOwner returns (uint256) {
        require(v != 0, 'VolatilityOracle: Volatility cannot be 0');

        lastVolatility = v;

        return v;
    }

    /*==== VIEWS ====*/

    /**
     * @notice Gets the volatility of dpx
     * @return volatility
     */
    function getVolatility() external view override returns (uint256) {
        require(lastVolatility != 0, 'VolatilityOracle: Last volatility == 0');

        return lastVolatility;
    }
}

contracts/oracle/RvOracle.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

// Contracts
import {Ownable} from '@openzeppelin/contracts/access/Ownable.sol';

// Interfaces
import {IVolatilityOracle} from '../interfaces/IVolatilityOracle.sol';

// Libraries
import {RealizedVolatility} from '../libraries/RealizedVolatility.sol';

contract RvOracle is Ownable, IVolatilityOracle {
    uint256 public points;
    uint256 public window;

    DpxOracle public dpxOracle;

    constructor(
        uint256 _points,
        uint256 _window,
        address _dpxOracle
    ) {
        points = _points;
        window = _window;
        dpxOracle = DpxOracle(_dpxOracle);
    }

    /**
     * @notice Updates points and window for rv sampling
     * @param _points The no. of points of pricing data to sample
     * @param _window The window size (x * period of oracle)
     */
    function updateVars(uint256 _points, uint256 _window) external onlyOwner {
        points = _points;
        window = _window;
    }

    /**
     * @notice Updates the dpx oracle
     * @param _dpxOracle The DPX oracle address
     */
    function updateOracle(address _dpxOracle) external onlyOwner {
        dpxOracle = DpxOracle(_dpxOracle);
    }

    /*==== VIEWS ====*/

    /**
     * @notice Gets the volatility of dpx
     * @return volatility
     */
    function getVolatility() external view override returns (uint256) {
        uint256 volatility = RealizedVolatility.calculateRv(
            dpxOracle.sample(1e18, points, window)
        );

        require(volatility != 0, 'VolatilityOracle: volatility cannot be 0');

        return volatility;
    }
}

interface DpxOracle {
    function sample(
        uint256 amountIn,
        uint256 points,
        uint256 window
    ) external view returns (uint256[] memory);
}

contracts/libraries/RealizedVolatility.s.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

library RealizedVolatility {
    uint256 private constant FIXED_1 = 0x080000000000000000000000000000000;
    uint256 private constant FIXED_2 = 0x100000000000000000000000000000000;
    uint256 private constant SQRT_1 = 13043817825332782212;
    uint256 private constant LOG_10_2 = 3010299957;
    uint256 private constant BASE = 1e10;

    function floorLog2(uint256 _n) public pure returns (uint8) {
        uint8 res = 0;

        if (_n < 256) {
            // At most 8 iterations
            while (_n > 1) {
                _n >>= 1;
                res += 1;
            }
        } else {
            // Exactly 8 iterations
            for (uint8 s = 128; s > 0; s >>= 1) {
                if (_n >= (uint256(1) << s)) {
                    _n >>= s;
                    res |= s;
                }
            }
        }

        return res;
    }

    function generalLog(uint256 x) public pure returns (uint256) {
        uint256 res = 0;

        // If x >= 2, then we compute the integer part of log2(x), which is larger than 0.
        if (x >= FIXED_2) {
            uint8 count = floorLog2(x / FIXED_1);
            x >>= count; // now x < 2
            res = count * FIXED_1;
        }

        // If x > 1, then we compute the fraction part of log2(x), which is larger than 0.
        if (x > FIXED_1) {
            for (uint8 i = 127; i > 0; --i) {
                x = (x * x) / FIXED_1; // now 1 < x < 4
                if (x >= FIXED_2) {
                    x >>= 1; // now 1 < x < 2
                    res += uint256(1) << (i - 1);
                }
            }
        }

        return (res * LOG_10_2) / BASE;
    }

    function sqrt(uint256 x) public pure returns (uint256 y) {
        uint256 z = (x + 1) / 2;
        y = x;
        while (z < y) {
            y = z;
            z = (x / z + z) / 2;
        }
    }

    function calculateRv(uint256[] memory p) public pure returns (uint256 x) {
        for (uint8 i = 1; i <= (p.length - 1); i++) {
            x +=
                (
                    (generalLog(p[i] * FIXED_1) -
                        generalLog(p[i - 1] * FIXED_1))
                ) **
                    2;
            //denom += FIXED_1**2;
        }
        //return (sum, denom);
        x = sqrt(uint256(252) * sqrt(x / (p.length - 1)));
        return (uint256(1e18) * x) / SQRT_1;
    }
}

